mapper.select:
SELECT D.DEPARTMENT_ID, D.DEPARTMENT_NAME, D.LOCATION_ID, D.MANAGER_ID, L.CITY, E.LAST_NAME FROM DEPARTMENTS D RIGHT OUTER JOIN LOCATIONS L ON D.LOCATION_ID = L.LOCATION_ID INNER JOIN EMPLOYEES E ON D.MANAGER_ID = E.EMPLOYEE_ID
MANAGER_ID = #{manager_id}
AND DEPARTMENT_NAME = #{department_name}
ORDER BY DEPARTMENT_ID

mapper.update:
 		UPDATE DEPARTMENTS SET DEPARTMENT_ID = #{department_id},
							   DEPARTMENT_NAME = #{department_name}, 
							   MANAGER_ID = #{manager_id},
							   LOCATION_ID = #{location_id}
							   WHERE DEPARTMENT_ID = #{department_id}
